mapper.select:
SELECT DISTINCT wi.*, fd.filePath, fd.fileNameThumb, wbi.boxIdx FROM wodBoxLinkInfo as wbi, wodInfo as wi
LEFT JOIN fileData AS fd ON wi.idx = fd.pIdx and fd.fileKind='C' and fd.tableLinkIdx=37 LEFT JOIN wodBoxLinkInfo AS bi ON wi.idx = bi.wodIdx WHERE wi.isOpen = 'Y' and wi.idx = fd.pIdx and wi.idx = wbi.wodIdx and wbi.boxIdx = #{boxIdx} and progressDate=DATE_FORMAT(CURDATE(), '%Y-%m-%d')
ORDER BY wi.lastUpdatetime DESC;

SELECT DISTINCT wi.*, fd.filePath, fd.fileNameThumb, wbi.boxIdx FROM wodBoxLinkInfo as wbi, wodInfo as wi
LEFT JOIN fileData AS fd ON wi.idx = fd.pIdx and fd.fileKind='C' and fd.tableLinkIdx=37 LEFT JOIN wodBoxLinkInfo AS bi ON wi.idx = bi.wodIdx WHERE wi.isOpen = 'Y' and wi.idx = fd.pIdx and wi.idx = wbi.wodIdx and wbi.boxIdx = #{boxIdx} and progressDate=DATE_FORMAT(DATE_ADD(CURDATE(), INTERVAL+1 DAY), '%Y-%m-%d')
ORDER BY wi.lastUpdatetime DESC;
