mapper.select:

SELECT AVG(value)
FROM wodItemRecordData
	WHERE wodItemRecordData.wodIdx = #{wodIdx}


SELECT MIN(value)
FROM wodItemRecordData 
WHERE wodItemRecordData.wodIdx = #{wodIdx}


	    Select *
			FROM stadion_db_dev.wodItemRecordData
			where  wodIdx = #{wodIdx}
			and status=1


             and gender = #{gender}


             and boxIdx = #{boxIdx}


             and gender = #{gender}


			GROUP BY accountIdx 

			ORDER by
			 value 

		    LIMIT #{count} OFFSET #{start};
