mapper.select:

SELECT count(*)
FROM wodParticipantLinkInfo
	WHERE wodParticipantLinkInfo.wbLinkIdx = #{idx} 
	and accountIdx=#{accountIdx}


SELECT status
FROM wodParticipantLinkInfo
	WHERE wodParticipantLinkInfo.wbLinkIdx = #{idx} 
	and accountIdx=#{accountIdx}


SELECT idx
FROM wodParticipantLinkInfo
	WHERE wodParticipantLinkInfo.wbLinkIdx = #{wbLinkIdx} 
	and accountIdx=#{accountIdx}
